<?php

namespace AppBundle\Repository;

/**
 * FirefighterRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class FirefighterRepository extends \Doctrine\ORM\EntityRepository
{
    public function findActiveFirefighterByPhoneNumber($number){

        $queryBuilder = $this->createQueryBuilder('o')
            ->where('o.enabled = :enabled')
            ->setParameter('enabled', true)
            ->andWhere('o.phoneNumber = :phoneNumber')
            ->setParameter('phoneNumber', $number)
            ->setMaxResults(1);
        return $queryBuilder->getQuery()->getOneOrNullResult();
    }        
}
